import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react'
import { getAll } from './services'

function firstWovelIndex(str1) {
  var vowel_list = 'aeiouyäö';
  var vcount = 0;

  for(var x = 0; x < str1.length ; x++) {
    if (vowel_list.indexOf(str1[x]) !== -1){
      vcount += 1;
      }
    }
  return vcount;
  }

function App() {

  const [ words, setWords] = useState([])
  const [ rhymingWords, setRhymingWords] = useState([])
  const [ rhyme, setRhyme ] = useState('')

  useEffect(() => {
    getAll().then(words =>
      setWords(words)
      )
    }, [])

  const findRhymes = (e) => { 
    setRhyme(e.target.value)
    setRhymingWords(words)
    }

  return (
    <div className="App">
      <header className="App-header">
        <p>
          <input type="text" value={rhyme} onChange={findRhymes}></input><br/>
          {rhyme}
        </p>
        {rhymingWords.map(word =>
          <p>{word.word}</p>
          )}
        <img src={logo} className="App-logo" alt="logo" />
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
